$(document).ready(function(){$(".offers__slider").slick({slidesToShow:1,mobileFirst:!0,arrows:!1,dots:!1,centerMode:!0,centerPadding:"30px",responsive:[{breakpoint:575,settings:{centerPadding:"100px"}},{breakpoint:767,settings:"unslick"}]}),$(".header__burger").click(function(){$(this).toggleClass("active"),$(".hidden-menu").fadeToggle()}),$(window).resize(function(){1300<=window.innerWidth?($(".header__burger").removeClass("active"),$(".hidden-menu").fadeOut()):window.innerWidth<=767&&$(".offers__slider").slick()})});